// =============================================================================
// HELPER FUNCTIONS
// =============================================================================

export function getTransitionById(id: string): TransitionRow | undefined {
  return TRANSITIONS.find((t) => t.id === id)
}

export function getTransitionsByFromCode(code: string): TransitionRow[] {
  return TRANSITIONS.filter((t) => t.from_code === code)
}

export function getTransitionsByToCode(code: string): TransitionRow[] {
  return TRANSITIONS.filter((t) => t.to_code === code)
}

export function getTransitionsByChangeCategory(category: string): TransitionRow[] {
  return TRANSITIONS.filter((t) => t.change_category === category)
}

export function getTransition(fromCode: string, toCode: string): TransitionRow | undefined {
  return TRANSITIONS.find((t) => t.from_code === fromCode && t.to_code === toCode)
}
