// =============================================================================
// HELPER FUNCTIONS
// =============================================================================

export function getStatusById(id: string): StatusRow | undefined {
  return STATUSES.find((s) => s.id === id)
}

export function getStatusByCode(code: string): StatusRow | undefined {
  return STATUSES.find((s) => s.code === code)
}

export function getStatusesByCategory(category: 'ACTIVE' | 'CLOSED'): StatusRow[] {
  return STATUSES.filter((s) => s.category === category)
}

export function getStatusesBySubcategory(subcategory: string): StatusRow[] {
  return STATUSES.filter((s) => s.subcategory === subcategory)
}

export function getStatusesByLifecycleStage(stage: string): StatusRow[] {
  return STATUSES.filter((s) => s.lifecycle_stage === stage)
}

export function getIgnorableStatuses(): StatusRow[] {
  return STATUSES.filter((s) => s.is_ignorable)
}

export function getNonIgnorableStatuses(): StatusRow[] {
  return STATUSES.filter((s) => !s.is_ignorable)
}
